                "<!DOCTYPE html>\n<html>\n<head>\n    <meta charset=\"UTF-8\">\n    <title>Enhanced Technician Work Report</title>\n    <style>\n        body { \n            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; \n            line-height: 1.6; \n            color: #333; \n            margin: 0; \n            padding: 0; \n            background-color: #f8f9fa;\n        }\n        .container { \n            max-width: 800px; \n            margin: 0 auto; \n            background-color: white; \n            box-shadow: 0 0 20px rgba(0,0,0,0.1);\n        }\n        .header { \n            background: linear-gradient(135deg, #28a745 0%, #20c997 100%); \n            color: white; \n            padding: 30px; \n            text-align: center;\n        }\n        .header h1 { \n            margin: 0; \n            font-size: 28px; \n            font-weight: 300;\n        }\n        .header .subtitle { \n            margin-top: 10px; \n            opacity: 0.9; \n            font-size: 16px;\n        }\n        .content { \n            padding: 30px;\n        }\n        .info-section { \n            background-color: #f8f9fa; \n            padding: 20px; \n            border-radius: 8px; \n            margin-bottom: 25px;\n            border-left: 4px solid #28a745;\n        }\n        .issue-section {\n            background-color: #fff3cd; \n            padding: 20px; \n            border-radius: 8px; \n            margin-bottom: 25px;\n            border-left: 4px solid #ffc107;\n        }\n        .work-section {\n            background-color: #d1ecf1; \n            padding: 20px; \n            border-radius: 8px; \n            margin-bottom: 25px;\n            border-left: 4px solid #17a2b8;\n        }\n        .info-grid { \n            display: grid; \n            grid-template-columns: 1fr 1fr; \n            gap: 20px; \n            margin-top: 15px;\n        }\n        .info-item { \n            background-color: white; \n            padding: 15px; \n            border-radius: 6px; \n            border: 1px solid #e9ecef;\n        }\n        .info-item strong { \n            color: #28a745; \n            display: block; \n            margin-bottom: 5px;\n        }\n        .issue-item {\n            background-color: white; \n            padding: 15px; \n            border-radius: 6px; \n            border: 1px solid #ffeaa7;\n            margin-bottom: 10px;\n        }\n        .work-item {\n            background-color: white; \n            padding: 15px; \n            border-radius: 6px; \n            border: 1px solid #bee5eb;\n            margin-bottom: 10px;\n        }\n        .status-resolved { color: #28a745; font-weight: bold; }\n        .status-pending { color: #ffc107; font-weight: bold; }\n        .status-critical { color: #dc3545; font-weight: bold; }\n        .priority-high { color: #dc3545; font-weight: bold; }\n        .priority-medium { color: #ffc107; font-weight: bold; }\n        .priority-low { color: #28a745; font-weight: bold; }\n        .footer { \n            background-color: #6c757d; \n            color: white; \n            padding: 20px; \n            text-align: center; \n            font-size: 14px;\n        }\n        .section-title {\n            color: #28a745;\n            font-size: 18px;\n            font-weight: bold;\n            margin-bottom: 15px;\n            display: flex;\n            align-items: center;\n        }\n        .section-title::before {\n            content: \"🔧\";\n            margin-right: 10px;\n            font-size: 20px;\n        }\n        .issue-title {\n            color: #856404;\n            font-size: 18px;\n            font-weight: bold;\n            margin-bottom: 15px;\n            display: flex;\n            align-items: center;\n        }\n        .issue-title::before {\n            content: \"⚠️\";\n            margin-right: 10px;\n            font-size: 20px;\n        }\n        .work-title {\n            color: #0c5460;\n            font-size: 18px;\n            font-weight: bold;\n            margin-bottom: 15px;\n            display: flex;\n            align-items: center;\n        }\n        .work-title::before {\n            content: \"✅\";\n            margin-right: 10px;\n            font-size: 20px;\n        }\n        .ticket-id {\n            background: linear-gradient(135deg, #28a745 0%, #20c997 100%);\n            color: white;\n            padding: 8px 16px;\n            border-radius: 20px;\n            font-weight: bold;\n            display: inline-block;\n            margin-bottom: 10px;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"header\">\n            <h1>Enhanced Technician Work Report</h1>\n            <div class=\"subtitle\">Professional Service Documentation</div>\n        </div>\n        \n        <div class=\"content\">\n            <!-- Technician Information Section -->\n            <div class=\"info-section\">\n                <div class=\"section-title\">Technician Information</div>\n                <div class=\"info-grid\">\n                    <div class=\"info-item\">\n                        <strong>Technician Name</strong>\n                        {{ TechnicianName }}\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Report Date</strong>\n                        {{ date.now }}\n                    </div>\n                </div>\n            </div>\n\n            <!-- Ticket Details Section -->\n            <div class=\"info-section\">\n                <div class=\"section-title\">Ticket Details</div>\n                <div class=\"ticket-id\">Ticket #{{ TicketId }}</div>\n                <div class=\"info-grid\">\n                    <div class=\"info-item\">\n                        <strong>Ticket Subject</strong>\n                        {{ TicketSubject }}\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Status</strong>\n                        <span class=\"status-{{ Status }}\">{{ Status }}</span>\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Customer</strong>\n                        {{ CustomerName }}\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Priority</strong>\n                        <span class=\"priority-{{ Priority | string.downcase }}\">{{ Priority }}</span>\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Created Date</strong>\n                        {{ CreatedAt }}\n                    </div>\n                    {{ if ResolvedAt }}\n                    <div class=\"info-item\">\n                        <strong>Resolved Date</strong>\n                        {{ ResolvedAt }}\n                    </div>\n                    {{ end }}\n                </div>\n            </div>\n\n            <!-- Issue Description Section -->\n            <div class=\"issue-section\">\n                <div class=\"issue-title\">Issue Description</div>\n                <div class=\"issue-item\">\n                    <strong>Problem Reported:</strong><br>\n                    {{ IssueDescription }}\n                </div>\n            </div>\n\n            <!-- Work Performed Section -->\n            <div class=\"work-section\">\n                <div class=\"work-title\">Work Performed</div>\n                <div class=\"work-item\">\n                    <strong>Technical Work Completed:</strong><br>\n                    {{ WorkPerformed }}\n                </div>\n            </div>\n\n            <!-- Resolution Summary -->\n            {{ if ResolvedAt }}\n            <div class=\"info-section\">\n                <div class=\"section-title\">Resolution Summary</div>\n                <div class=\"info-grid\">\n                    <div class=\"info-item\">\n                        <strong>Resolution Status</strong>\n                        <span class=\"status-resolved\">Resolved</span>\n                    </div>\n                    <div class=\"info-item\">\n                        <strong>Resolution Date</strong>\n                        {{ ResolvedAt }}\n                    </div>\n                </div>\n            </div>\n            {{ end }}\n        </div>\n        \n        <div class=\"footer\">\n            <p>&copy; 2025 Customer Portal. All rights reserved.</p>\n            <p>Generated on {{ date.now }}</p>\n        </div>\n    </div>\n</body>\n</html>\n"
            )
        };

        await _reportTemplateRepository.InsertManyAsync(reportTemplatelist);
